var ListController = require("./ListController");
var inherit = require("../utils/inherit");
var RequestManager = ("../utils/RequestManager");

module.exports = TrackListController;

function TrackListController(observer){
    ListController.call(this, observer);
}

inherit(TrackListController, ListController);

TrackListController.prototype.fetchData = function(){
    RequestManager(this._fetchDataHandler.bind(this ));
};

TrackListController.prototype.add = function(projectName, data){
    RequestManager(projectName, data, this._addHandler.bind(this));
};

TrackListController.prototype.remove = function(projectName){
    RequestManager(projectName, this._removeHandler.bind(this));
};
