module.exports = FilterModel;

function FilterModel(){
    this.isUsed = false;
    this.filterObject = this.generate();
}

FilterModel.prototype.generate = function(){};

FilterModel.prototype.getOptions = function(){};

FilterModel.prototype.setOptions = function(){};

FilterModel.prototype.applyToTrack = function(track){
    this.filterObject.toMaster();
    track.connect(this.filterObject);
};
