var BaseDrumModel = require("./BaseDrumModel");
var inherit = require("../utils/inherit");

module.exports = MembraneModel;

function MembraneModel(setting, playSetting){
    BaseDrumModel.call(this, setting, playSetting);
}

inherit(MembraneModel, BaseDrumModel);

MembraneModel.prototype.generate = function(){
    return new Tone.MembraneSynth(this.setting);
};

MembraneModel.prototype.play = function(note, startTime){
    this.trackObject.triggerAttackRelease(note, startTime);
};

MembraneModel.prototype.playAll = function(){
    var token;
    for(token in this.playSetting){
        this.play(this.playSetting[token].note, this.playSetting[token].startTime);
    }
};
