var RequestManager = require("../utils/RequestManager");
var eventListener = require("./eventListener.js");

module.exports = ProjectModel;

function ProjectModel(){
    this.data = [];
}

ProjectModel.prototype.fetchData = function(){
    // Request manager should be static!!!!
    var requestManager = new RequestManager();
    requestManager.getProjectLIst(function(response){
        if (response instanceof Error){
            // Smth bad is happening!!
        } else{
            // sends itself:
            this.data = response;
            eventListener.notify("PROJECT_MODEL_UPDATED", this);
        }
    });
}
