var inherit = require("../utils/inherit");
var Factory = require("./Factory");
var BaseView = require("./BaseView");
var eventListener = require("../model/eventListener");

module.exports = InstrumentView;

function InstrumentView(imageSource, title){
    BaseView.call(this, "instrument-view");

    this.image = $("<img src='" + imageSource + "'>");
    this.title = $("<h5 class='ui header'>" + title + "</h5>");

    this._build();
}

inherit(InstrumentView, BaseView);

InstrumentView.prototype._build = function(){
    eventListener.subscribe("SHOW_INSTRUMENT_VIEW", this.show.bind(this));

    this.getContainer().append(this.image);
    this.getContainer().append(this.title);
};



